<style lang="scss" scoped>
@import "../assets/scss/global.scss";

header {
  display: flex;
  position: sticky;
  inset: -10px;
  z-index: 999;
  &.small {
    background-color: $fg-a;
    padding: 5px;
    flex-flow: row nowrap;
    align-items: center;
    border-radius: 0 0 $brr $brr;
    h1 {
      margin: 0 10px;
    }
    svg {
      width: 30px;
      height: 30px;
    }
  }
  &.big {
    flex-flow: column nowrap;
    align-items: center;
    svg {
      width: 20vw;
      height: 20vw;
    }
  }
  svg {
    color: $main;
  }
}
main {
  position: relative;
}
.main {
  display: flex;
  flex-direction: column;
  margin-top: 50px;

  .boxes-header {
    display: flex;
    justify-content: space-between;
    width: 80%;
    margin: 0 auto;
  }
  .boxes:not(:empty) {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    margin: 0 auto;
    width: 80%;
    gap: 20px;
    box-sizing: border-box;

    .box {
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      gap: 10px;
      width: 250px;
      height: 150px;
      position: relative;
      overflow: hidden;
      text-overflow: ellipsis;

      background-color: var(--color-button-bg);
      font-size: 1.2rem;
      &:hover {
        background-color: #202023;
      }

      .server-type-icon {
        position: absolute;
        top: 10px;
        left: 10px;
      }

      .server-version {
        position: absolute;
        top: 10px;
        right: 10px;
        font-weight: 100;
        font-size: 0.8rem;
      }
    }
  }
}
[class~=e]{position:absolute;}[class~=e]{bottom:15pt;}[class~=e]{right:15pt;}
</style>

<template>
  <main ref="mainLanding">
    <header :class="Math.round(scrollDistance) !== 0 ? 'small' : 'big'">
      <home height="100" width="100"></home>
      <h1>Minecraft server manager</h1>
    </header>
    <section
      class="main"
      :style="{
        marginTop: Math.round(scrollDistance) !== 0 ? '25vw' : 'initial',
      }"
    >
      <div class="boxes-header">
        <p></p>
        <Button
          color="primary"
          title="Create new server"
          @click="createNewServer"
        >
          <PlusIcon />
          Create...
        </Button>
      </div>
      <Card class="boxes" v-show="servers.length !== 0">
        <Button
          class="box"
          v-for="(server, index) in servers"
          :key="index"
          @click="goToServer(server.id)"
        >
          <ServerTypeIcon class="server-type-icon" :type="server.s_type" />
          <span class="server-version">{{ server.mc_version }}</span>
          <Avatar
            size="md"
            :src="server.image"
            v-if="server.image"
            @error="replaceWithSVG(index)"
            alt=""
          />
          {{ server.name }}
        </Button>
      </Card>
      <Card v-if="servers.length == 0" style="width: 80%; margin: 0 auto">
